// импорт библиотеки Moment.js
import moment from "./moment.js";

// функция для получения текущей даты и времени
const getCurrentDateTime = () => moment().format('YYYY-MM-DD HH:mm:ss');

// функция для получения разницы между двумя датами
const getDifferenceBetweenDates = (date1, date2) => {
    const d1 = moment(date1, 'YYYY-MM-DD');
    const d2 = moment(date2, 'YYYY-MM-DD');
    return d2.diff(d1, 'days');
}

// функция для форматирования даты
const formatDate = (date, format) => moment(date).format(format);

// экспорт функций
export {
    getCurrentDateTime,
    getDifferenceBetweenDates,
    formatDate
};
